        // echo 'loaded a record ...';

        $currentType = FormUtil::getPassedValue('type', 'user', 'GETPOST', FILTER_SANITIZE_STRING);
        $currentFunc = FormUtil::getPassedValue('func', 'main', 'GETPOST', FILTER_SANITIZE_STRING);

        $this['id'] = (int) ((isset($this['id']) && !empty($this['id'])) ? DataUtil::formatForDisplay($this['id']) : 0);
    if ($currentFunc != 'edit') {
        $this['name'] = ((isset($this['name']) && !empty($this['name'])) ? DataUtil::formatForDisplayHTML($this['name']) : '');
    }
    if ($currentFunc != 'edit') {
        $this['controllerForView'] = ((isset($this['controllerForView']) && !empty($this['controllerForView'])) ? DataUtil::formatForDisplayHTML($this['controllerForView']) : '');
    }
    if ($currentFunc != 'edit') {
        $this['controllerForSingleObject'] = ((isset($this['controllerForSingleObject']) && !empty($this['controllerForSingleObject'])) ? DataUtil::formatForDisplayHTML($this['controllerForSingleObject']) : '');
    }
    if ($currentFunc != 'edit') {
        $this['parameterForObjects'] = ((isset($this['parameterForObjects']) && !empty($this['parameterForObjects'])) ? DataUtil::formatForDisplayHTML($this['parameterForObjects']) : '');
    }
    if ($currentFunc != 'edit') {
        $this['nameOfIdentifier'] = ((isset($this['nameOfIdentifier']) && !empty($this['nameOfIdentifier'])) ? DataUtil::formatForDisplayHTML($this['nameOfIdentifier']) : '');
    }
    if ($currentFunc != 'edit') {
        $this['extraIdentifier'] = ((isset($this['extraIdentifier']) && !empty($this['extraIdentifier'])) ? DataUtil::formatForDisplayHTML($this['extraIdentifier']) : '');
    }
        $this->prepareItemActions();
        return true;
    }

